import{_ as e,c as a,o as t,ae as i}from"./chunks/framework.7E1nAy7n.js";const u=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"collect/mysql/函数.md","filePath":"collect/mysql/函数.md"}'),r={name:"collect/mysql/函数.md"};function d(o,l,s,n,c,p){return t(),a("div",null,[...l[0]||(l[0]=[i('<h2 id="函数是什么" tabindex="-1">函数是什么？ <a class="header-anchor" href="#函数是什么" aria-label="Permalink to &quot;函数是什么？&quot;">​</a></h2><p>函数是指一段可以被调用的程序和代码。</p><h2 id="函数的分类" tabindex="-1">函数的分类 <a class="header-anchor" href="#函数的分类" aria-label="Permalink to &quot;函数的分类&quot;">​</a></h2><ol><li>字符串函数</li><li>数值函数</li><li>日期函数</li><li>流程函数</li></ol><h2 id="字符串函数" tabindex="-1">字符串函数 <a class="header-anchor" href="#字符串函数" aria-label="Permalink to &quot;字符串函数&quot;">​</a></h2><ol><li>concat(s1,s2,...,sn) 连接字符串</li><li>insert(str,x,y,instr) 字符串替换</li><li>lower(str) 转小写</li><li>upper(str) 转大写</li><li>left(str,x) 左截取</li><li>right(str,x) 右截取</li><li>lpad(str,n,pad) 左填充</li><li>rpad(str,n,pad) 右填充</li><li>trim(str) 去头尾空格</li><li>replace(str,a,b) 替换</li><li>substring(str,start,length) 截取字符串从start开始，长度为length的子串</li></ol><p>select concat(&#39;hello&#39;,&#39;world&#39;);</p><p>update user set userId = lpad(userId`, 6, &#39;0&#39;);</p><h2 id="数值函数" tabindex="-1">数值函数 <a class="header-anchor" href="#数值函数" aria-label="Permalink to &quot;数值函数&quot;">​</a></h2><ol><li>ceil(x) 向上取整</li><li>floor(x) 向下取整</li><li>round(x,y) 四舍五入，保留y位小数</li><li>mod(x,y) 求模</li><li>rand() 生成0-1内的随机数</li></ol><p>生成随机6位验证码：</p><p>select lpad(round(rand()*1000000,0), 6, &#39;0&#39;);</p><h2 id="日期函数" tabindex="-1">日期函数 <a class="header-anchor" href="#日期函数" aria-label="Permalink to &quot;日期函数&quot;">​</a></h2><ol><li>curdate() 当前日期</li><li>curtime() 当前时间</li><li>now() 当前日期和时间</li><li>year(date) 年</li><li>month(date) 月</li><li>day(date) 日</li><li>date_format(date,fmt) 按照指定格式输出日期</li><li>datediff(date1,date2) 两个日期的差值</li><li>date_add(date,interval expr type) 日期加减</li></ol><p>select now();</p><p>select date_format(now(), &#39;%Y年%m月%d日 %H时%i分%s秒&#39;);</p><p>select datediff(&#39;2022-01-01&#39;, &#39;2021-01-01&#39;);</p><p>select date_add(now(), interval 1 day);</p><h2 id="流程函数" tabindex="-1">流程函数 <a class="header-anchor" href="#流程函数" aria-label="Permalink to &quot;流程函数&quot;">​</a></h2><ol><li>if(value,t,f) 如果value为true，则返回t，否则返回f</li><li>ifnull(value1,value2) 如果value1不为空，则返回value1，否则返回value2</li><li>case when [val1] then [res1] ... else [default] end 如果val1为true，则返回res1，否则返回default</li><li>case [expr] when [val1] then [res1] ... else [default] end 如果expr的值等于val1，则返回res1，否则返回default</li></ol>',20)])])}const f=e(r,[["render",d]]);export{u as __pageData,f as default};
