import{_ as i,c as l,o as e,ae as t}from"./chunks/framework.CvsoPZUE.js";const p=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"note/AI/audit/安全依赖审计工具.md","filePath":"note/AI/audit/安全依赖审计工具.md"}'),o={name:"note/AI/audit/安全依赖审计工具.md"};function s(n,a,d,r,u,h){return e(),l("div",null,[...a[0]||(a[0]=[t('<h2 id="什么是安全依赖审计" tabindex="-1">什么是安全依赖审计 <a class="header-anchor" href="#什么是安全依赖审计" aria-label="Permalink to &quot;什么是安全依赖审计&quot;">​</a></h2><p>安全依赖审计是指对某个工程的所有直接依赖和间接依赖进行安全验证，发现其是否包含潜藏的风险。</p><h2 id="何时会进行安全依赖审计" tabindex="-1">何时会进行安全依赖审计 <a class="header-anchor" href="#何时会进行安全依赖审计" aria-label="Permalink to &quot;何时会进行安全依赖审计&quot;">​</a></h2><ul><li>技术选型： <ul><li>针对目标技术</li><li>远程审计</li></ul></li><li>项目开发 <ul><li>针对当前工程</li><li>本地审计/远程审计</li></ul></li></ul><h2 id="如何进行安全审计" tabindex="-1">如何进行安全审计 <a class="header-anchor" href="#如何进行安全审计" aria-label="Permalink to &quot;如何进行安全审计&quot;">​</a></h2><div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">npm</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> audit</span></span></code></pre></div><h2 id="为什么不直接使用-npm-audit" tabindex="-1">为什么不直接使用 npm audit <a class="header-anchor" href="#为什么不直接使用-npm-audit" aria-label="Permalink to &quot;为什么不直接使用 npm audit&quot;">​</a></h2><p><code>npm audit</code> 的问题：</p><ul><li>阅读不友好 <ul><li>依赖关系不清晰</li></ul></li><li>功能不完整 <ul><li>无法对远程仓库进行审计</li><li>无法对工程本身进行审计（只能审计依赖）</li></ul></li><li>难以集成 <ul><li>AI应用集成：取决于应用是否支持运行命令</li><li>CI/CD集成：无法定义部署决策逻辑（怎么定义过没过）</li></ul></li></ul><h2 id="需求" tabindex="-1">需求 <a class="header-anchor" href="#需求" aria-label="Permalink to &quot;需求&quot;">​</a></h2><p>自定义安全审计功能，该功能可支持：</p><ul><li>对本地工程或远程仓库均能进行安全审计</li><li>安全审计时能够对工程本身进行审计</li><li>审计结果中包含清晰的依赖路径（比如某个包出现安全漏洞，这个包出现在哪里，在哪些依赖链条上面）</li><li>审计的结果是一个统一标准的 <code>markdown</code> 格式文档</li><li>支持 <code>MCP Server</code> 协议</li><li>更多的拓展功能...</li></ul>',12)])])}const _=i(o,[["render",s]]);export{p as __pageData,_ as default};
