import{_ as i,c as a,o as e,ae as t}from"./chunks/framework.CvsoPZUE.js";const c=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"note/mysql/mysql简述.md","filePath":"note/mysql/mysql简述.md"}'),r={name:"note/mysql/mysql简述.md"};function o(s,l,n,h,d,m){return e(),a("div",null,[...l[0]||(l[0]=[t('<h2 id="为什么要学习-mysql" tabindex="-1">为什么要学习 MYSQL ？ <a class="header-anchor" href="#为什么要学习-mysql" aria-label="Permalink to &quot;为什么要学习 MYSQL ？&quot;">​</a></h2><ol><li>MySQL 是世界上最流行的开源关系型数据库。</li><li>功能强大，适合系统大多数应用场景，性能高，支持事事务保证数据的一致性。</li><li>职业发展的必备技能：后端开发（Java/Python/PHP）、全栈开发等开发岗位通常要求掌握 MYSQL。运维和 DBA 岗位也是如此。</li><li>成本低：社区版开源免费，足够中小型企业或者个人学习使用，如需要更加强大的功能和技术支持可以使用付费的商业版。</li><li>跨平台、丰富学习资源：支持 windows、Linux、macOS。官方文档完善，社区活跃。</li></ol><h2 id="基础篇" tabindex="-1">基础篇 <a class="header-anchor" href="#基础篇" aria-label="Permalink to &quot;基础篇&quot;">​</a></h2><ul><li>MySQL 概述</li><li>SQL</li><li>函数</li><li>约束</li><li>多表查询</li><li>事务</li></ul><h2 id="进阶篇" tabindex="-1">进阶篇 <a class="header-anchor" href="#进阶篇" aria-label="Permalink to &quot;进阶篇&quot;">​</a></h2><ul><li>存储引擎</li><li>索引</li><li>SQL 优化</li><li>视图/存储过程/触发器</li><li>锁</li><li>InnoDB 核心</li><li>MYSQL 管理</li></ul><h2 id="运维篇" tabindex="-1">运维篇 <a class="header-anchor" href="#运维篇" aria-label="Permalink to &quot;运维篇&quot;">​</a></h2><ul><li>日志</li><li>主从复制</li><li>分库分表</li><li>读写分离</li></ul><h2 id="数据库相关概念" tabindex="-1">数据库相关概念 <a class="header-anchor" href="#数据库相关概念" aria-label="Permalink to &quot;数据库相关概念&quot;">​</a></h2><ol><li>数据库(Database，简称 DB)：存储数据的仓库。</li><li>数据库管理系统(Database Management System，简称 DBMS)：用于管理和操纵数据库的大型软件。</li><li>SQL(Structured Query Language，简称 SQL)：用于和关系型数据库通信的语言，定义了一套操作关系型数据库的统一标准。</li><li>主流的关系型数据库管理系统：</li></ol><ul><li>Oracle：大型数据库，收费，价格高，性能强，安全性高，适用于大型企业。</li><li>MYSQL：中小型数据库，性能强，适用于中小型企业。分为社区版和商业版，社区版免费，商业版收费，一般中小型企业或者个人学习使用社区版就足够了。</li><li>Microsoft SQL Server：微软开发的中型数据库，收费。</li><li>PostgreSQL：开源免费的中小型数据库。</li><li>IBM Db2: 收费。</li><li>SQLite：轻量级数据库，开源免费，嵌入式的数据库，适合桌面和移动应用。</li></ul><h2 id="学的是mysql-以后用其它的数据库怎么办" tabindex="-1">学的是mysql，以后用其它的数据库怎么办？ <a class="header-anchor" href="#学的是mysql-以后用其它的数据库怎么办" aria-label="Permalink to &quot;学的是mysql，以后用其它的数据库怎么办？&quot;">​</a></h2><p>SQL 是关系型数据的统一操作标准。</p>',13)])])}const q=i(r,[["render",o]]);export{c as __pageData,q as default};
